package day13_methods_Part1;

public class GoogleSearch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		navigateGoogle();
		searchForShoes();
		printResults();
		
		
	}

	
	
	public static void navigateGoogle() {
		System.out.println("launch crome browser");
		System.out.println("navigate to google");
		
	}
	
	public static void searchForShoes() {
		System.out.println("type shoes into search field");
		System.out.println("click on the search button");
	}
	public static void printResults() {
		System.out.println("result for shoes...");
	}
}
