package day13_methods_Part1;

public class task56 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		convertKM();
	}

	public static void convertKM (){
		int miles=80;
		double km;		
		
		km=miles*1.609; 
		System.out.println(miles+" miles = "+km+" km");
	}
	
}
